@implementation ThemeRenkoo

@synthesize borderColor;

- (id)initWith:(byte const*[2])data len:(usize const [2])len {
    self = [super init];
    
    NgImage* img = NgImageLoadPngData(data[0], len[0]);
    img_indicator = [NgFill fillWithImage:img]; 
    
    img = NgImageLoadPngData(data[1], len[1]);
    img_background = [NgFill fillWithImage:img];
    
    return self;
}

- (void)dealloc {    
    [super dealloc];
}

- (void)setInLayer:(CALayer *)layer {    
    layer.backgroundColor = self.background;
        
    NgLayer *ly_indicator = [[NgLayer alloc] init];
    ly_indicator.backgroundFill = img_indicator;
    [layer insertSublayer:ly_indicator atIndex:0];
    [ly_indicator release];
    
    NgLayer *ly_background = [[NgLayer alloc] init];
    ly_background.backgroundFill = img_background;
    ly_background.masksToBounds = YES;
    ly_background.cornerRadius = 5;
    ly_background.borderWidth = 2;
    ly_background.borderColor = borderColor;
    [layer insertSublayer:ly_background atIndex:0];
    [ly_background release];
    
    ::nnt::CGRectLayoutHBox lyt(layer.bounds);
    ::nnt::CGRectLayoutVBox lyt_indicator(lyt.add_pixel(12));
    
    CGRect rc_message = lyt.fill();
    rc_message.origin.y += 2;
    rc_message.origin.x -= 2;
    rc_message.size.height -= 4;
    ::nnt::CGRectLayoutHBox lyt_message(rc_message);
    
    // set indicator.
    CGRect rc_indicator = lyt_indicator.add_pixel(12);
    rc_indicator.origin.y += 8;
    ly_indicator.frame = rc_indicator;
    
    // set message.
    ly_background.frame = lyt_message.get_rc();
}

@end

@implementation ThemeRenkooGreen

- (id)init {    
    self = [super initWith:data_green len:ldata_green];
    
    self.borderColor = [UIColor colorWithRGB:0xc7e092].CGColor;
    
    return self;
}

@end

NNT_END_OBJC